
CREATE DATABASE paul_tergeist;
use paul_tergeist;
    CREATE TABLE story (id INTEGER NOT NULL AUTO_INCREMENT PRIMARY KEY,
    texte TEXT NOT NULL, visible BOOLEAN DEFAULT false, encore BOOLEAN DEFAULT true) ENGINE=InnoDB;
    CREATE TABLE user (id INTEGER NOT NULL AUTO_INCREMENT PRIMARY KEY,
    nom VARCHAR(30), prenom VARCHAR(30), email VARCHAR(30) UNIQUE, motdepasse VARCHAR(50), position INTEGER NOT NULL DEFAULT 0) ENGINE=InnoDB;
    CREATE TABLE ref (id INTEGER NOT NULL AUTO_INCREMENT PRIMARY KEY,
    titre VARCHAR(100) NOT NULL, info VARCHAR(300), picture VARCHAR(500), audiovideo VARCHAR(500) DEFAULT NULL);
    CREATE TABLE personnage (id INTEGER NOT NULL AUTO_INCREMENT PRIMARY KEY,
    nom VARCHAR(50) NOT NULL, prenom VARCHAR(50), quality VARCHAR(50), borrowed BOOLEAN DEFAULT true, color VARCHAR(50), style VARCHAR(100));
    CREATE TABLE next_story (current_id INTEGER NOT NULL, next_id INTEGER, FOREIGN KEY (current_id) REFERENCES story (id) ON DELETE RESTRICT,
    FOREIGN KEY (next_id) REFERENCES story (id) ON DELETE RESTRICT, PRIMARY KEY (current_id, next_id));
